import React, { useState, useEffect } from "react";
import { Form, Button, Container } from "react-bootstrap";
import axios from "axios";

function SearchScreen() {
  const [searchSystem, setSearchSystem] = useState("");
  const [region, setRegion] = useState("");
  const [keyword, setKeyword] = useState("");

  const onSearchSystemChange = (e) => {
    setSearchSystem(e.target.value);
  };

  const onRegionChange = (e) => {
    setRegion(e.target.value);
  };

  const onKeywordChange = (e) => {
    setKeyword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      search_system: React.state.search_system,
      region: React.state.region,
      keyword: React.state.keyword,
    };
    axios
      .post("http://127.0.0.1:8000/api/create_task/", data)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

  return (
    <div className="search-form">
      <h1>Create Task</h1>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formSearchSystem">
            <Form.Label>Select Search System</Form.Label>
            <Form.Control
              as="select"
              value={React.state.search_system}
              required
              onChange={onSearchSystemChange}
            >
              <option>Google</option>
              <option>Bing</option>
              <option>Yahoo</option>
              <option>Yandex</option>
              <option>Baidu</option>
              <option>Naver</option>
            </Form.Control>
            {/* <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text> */}
          </Form.Group>
          <Form.Group controlId="formRegion">
            <Form.Label>Select Region</Form.Label>
            <Form.Control
              as="select"
              value={React.state.region}
              required
              onChange={onRegionChange}
            >
              <option>United States</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="formKeyword">
            <Form.Control
              type="text"
              placeholder="Enter your keyword"
              value={React.state.keyword}
              onChange={onKeywordChange}
              required
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </Container>
    </div>
  );
}

export default SearchScreen;
