import React, { useState, useEffect } from "react";
import { Form, Button, Container } from "react-bootstrap";
import axios from "axios";
import Product from "../components/Task";

function SearchScreen() {
  const [searchSystem, setSearchSystem] = useState("Google");
  const [region, setRegion] = useState("United States");
  const [keyword, setKeyword] = useState([]);
  const [resp, setResp] = useState([]);

  const onSearchSystemChange = (e) => {
    setSearchSystem(e.target.value);
  };

  const onRegionChange = (e) => {
    setRegion(e.target.value);
  };

  const onKeywordChange = (e) => {
    setKeyword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      search_system: searchSystem,
      region: region,
      keyword: keyword,
    };
    const { response } = axios.post(
      "http://127.0.0.1:8000/api/create_task/",
      data
    );
    async function fetchProducts() {
      const { datas } = await axios.get("/api/products/");
      setProducts(datas);
    }

    console.log(datas);
  };

  return (
    <div className="search-form">
      <h1>Create Task</h1>
      <Container>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formSearchSystem">
            <Form.Label>Select Search System</Form.Label>
            <Form.Control
              as="select"
              value={searchSystem}
              required
              onChange={onSearchSystemChange}
            >
              <option>Google</option>
              <option>Bing</option>
              <option>Yahoo</option>
              <option>Yandex</option>
              <option>Baidu</option>
              <option>Naver</option>
            </Form.Control>
            {/* <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text> */}
          </Form.Group>
          <Form.Group controlId="formRegion">
            <Form.Label>Select Region</Form.Label>
            <Form.Control
              as="select"
              value={region}
              required
              onChange={onRegionChange}
            >
              <option>United States</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="formKeyword">
            <Form.Control
              type="text"
              placeholder="Enter your keyword"
              value={keyword}
              onChange={onKeywordChange}
              required
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
        {resp.length <= 0 ? null : (
          <div className="resp-div">
            <h1>Task created!</h1>
            <Product product={resp} />
          </div>
        )}
      </Container>
    </div>
  );
}

export default SearchScreen;
