import React, { useState, useEffect } from "react";
import { Form, Button, Container } from "react-bootstrap";
import axios from "axios";

function SearchScreen() {
  React.state = {
    search_system: "",
    region: "",
    keyword: "",
  };

  const onSearchSystemChange = (e) => {
    this.setState({
      search_system: e.target.value,
    });
  };

  const onRegionChange = (e) => {
    this.setState({
      region: e.target.value,
    });
  };

  const onKeywordChange = (e) => {
    this.setState({
      keyword: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      title: this.state.title,
      body: this.state.body,
    };
    axios
      .post("https://jsonplaceholder.typicode.com/posts", data)
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

  return (
    <div className="search-form">
      <h1>Create Task</h1>
      <Container>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group
            controlId="formBasicEmail"
            onChange={onSearchSystemChange}
          >
            <Form.Label>Select Search System</Form.Label>
            <Form.Control
              as="select"
              value={this.state.search_system}
              required="True"
            >
              <option>Google</option>
              <option>Bing</option>
              <option>Yahoo</option>
              <option>Yandex</option>
              <option>Baidu</option>
              <option>Naver</option>
            </Form.Control>
            {/* <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text> */}
          </Form.Group>
          <Form.Group controlId="formBasicPassword" onChange={onRegionChange}>
            <Form.Label>Select Region</Form.Label>
            <Form.Control
              as="select"
              value={this.state.search_system}
              required="True"
            >
              <option>United States</option>
            </Form.Control>
          </Form.Group>
          <Form.Group onChange={onRegionChange}>
            <Form.Control type="text" placeholder="Enter your keyword" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </Container>
    </div>
  );
}

export default SearchScreen;
